import React, { useState, useEffect } from 'react';
import './Menu.css';
import { Card, CardBody, CardText, CardTitle, Button, Form } from 'react-bootstrap';
import BreakfastImg from '../utils/img/breakfast.jpg';
import LunchImg from '../utils/img/lunch.jpg';
import DinnerImg from '../utils/img/dinner.jpg';
import DessertImg from '../utils/img/dessert.jpg';

const Menu = () => {
    const [breakfast, setBreakfast] = useState([]);
    const [lunch, setLunch] = useState([]);
    const [dinner, setDinner] = useState([]);
    const [dessert, setDessert] = useState([]);

    useEffect(() => {
        fetchMenuData();
    }, []);

    const fetchMenuData = async () => {
        try {
            // Fetching the menu data from each category
            const responseBreakfast = await fetch('/api/menu/breakfast');
            const responseLunch = await fetch('/api/menu/lunch');
            const responseDinner = await fetch('/api/menu/dinner');
            const responseDessert = await fetch('/api/menu/dessert');

            // Check if the responses are successful (status code 200-299)
            if (!responseBreakfast.ok || !responseLunch.ok || !responseDinner.ok || !responseDessert.ok) {
                throw new Error('Failed to fetch menu data');
            }

            // Parse the JSON responses
            const breakfastData = await responseBreakfast.json();
            const lunchData = await responseLunch.json();
            const dinnerData = await responseDinner.json();
            const dessertData = await responseDessert.json();

            setBreakfast(breakfastData);
            setLunch(lunchData);
            setDinner(dinnerData);
            setDessert(dessertData);

        } catch (error) {
            console.error('Error fetching menu data:', error);
            // Optionally, you can display an error message to the user here
        }
    };


    // PATCH - Update an existing menu item
    const updateMenuItem = async (category, id, updatedItem) => {
        const response = await fetch(`/api/menu/${category}/${id}`, {
            method: 'PATCH',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(updatedItem),
        });

        if (response.ok) {
            fetchMenuData(); // Refresh the menu data after updating an item
        }
    };

    // DELETE - Remove a menu item
    const deleteMenuItem = async (category, id) => {
        const response = await fetch(`/api/menu/${category}/${id}`, {
            method: 'DELETE',
        });

        if (response.ok) {
            fetchMenuData(); // Refresh the menu data after deleting an item
        }
    };

    return (
        <div className="menu-page">
            <header className="mt-5">
                <div className="container h-100 d-flex align-items-center justify-content-center">
                    <h1 className="text-light">Menu</h1>
                </div>
            </header>

            <div className="breakfast my-5">
                <div className="container">
                    <h2 className="text-center fs-1 mb-4 mb-lg-5 text-uppercase fw-bold text-success">Breakfast</h2>
                    <div className="row flex-column-reverse flex-lg-row">
                        <div className="col-lg-6 d-flex justify-content-center">
                            <img src={BreakfastImg} className="img-fluid w-75 mt-4 mt-lg-0" alt="" />
                        </div>
                        <div className="col-lg-6 d-flex flex-column justify-content-around">
                            {breakfast.map((item) => (
                                <div key={item.id}>
                                    <Card className="border-0">
                                        <CardBody>
                                            <CardTitle className="text-center fs-3">{item.name}</CardTitle>
                                            <CardText className="text-center fs-5">{item.description}</CardText>
                                            <CardText className="text-center fs-3 fw-bold text-success">{item.price}</CardText>
                                            <div className="text-center">
                                                <Button
                                                    variant="danger"
                                                    onClick={() => deleteMenuItem('breakfast', item.id)}
                                                >
                                                    Delete
                                                </Button>
                                                <Button
                                                    variant="warning"
                                                    onClick={() => updateMenuItem('breakfast', item.id, { price: '£10' })}
                                                >
                                                    Update Price
                                                </Button>
                                            </div>
                                        </CardBody>
                                    </Card>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Add New Menu Item (POST) */}
            <div className="add-item-form">
                <h3 className="text-center">Add New Breakfast Item</h3>
                <Form
                    onSubmit={(e) => {
                        e.preventDefault();
                        const form = e.target;
                        const newItem = {
                            name: form.name.value,
                            description: form.description.value,
                            price: form.price.value,
                        };
                        addMenuItem('breakfast', newItem);
                    }}
                >
                    <Form.Group>
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" name="name" required />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" name="description" required />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Price</Form.Label>
                        <Form.Control type="text" name="price" required />
                    </Form.Group>
                    <Button type="submit" variant="success" className="mt-3">
                        Add Item
                    </Button>
                </Form>
            </div>

            {/* Repeat the above for lunch, dinner, and dessert sections */}
            {/* Similar code would go for the lunch, dinner, and dessert sections */}
        </div>
    );
};

export default Menu;
