import React, { useState, useEffect } from 'react';
import './Menu.css';
import { Card, CardBody, CardText, CardTitle, Button } from 'react-bootstrap';
import BreakfastImg from '../utils/img/breakfast.jpg';
import LunchImg from '../utils/img/lunch.jpg';
import DinnerImg from '../utils/img/dinner.jpg';
import DessertImg from '../utils/img/dessert.jpg';

function Menu() {
    const [breakfast, setBreakfast] = useState([]);
    const [lunch, setLunch] = useState([]);
    const [dinner, setDinner] = useState([]);
    const [dessert, setDessert] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        const resBreakfast = await fetch('http://localhost:3000/breakfast');
        const resLunch = await fetch('http://localhost:3000/lunch');
        const resDinner = await fetch('http://localhost:3000/dinner');
        const resDessert = await fetch('http://localhost:3000/dessert');

        setBreakfast(await resBreakfast.json());
        setLunch(await resLunch.json());
        setDinner(await resDinner.json());
        setDessert(await resDessert.json());
    };

    const addItem = async (category, item) => {
        const res = await fetch(`http://localhost:3000/${category}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(item),
        });
        const newItem = await res.json();
        fetchData(); // Reload data
    };

    const updateItem = async (category, id, updatedItem) => {
        const res = await fetch(`http://localhost:3000/${category}/${id}`, {
            method: 'PATCH',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(updatedItem),
        });
        const updatedData = await res.json();
        fetchData(); // Reload data
    };

    const deleteItem = async (category, id) => {
        const res = await fetch(`http://localhost:3000/${category}/${id}`, {
            method: 'DELETE',
        });

        if (res.ok) {
            fetchData(); // Reload data after deletion
        }
    };

    return (
        <div className='menu-page'>
            <header className='mt-5'>
                <div className='container h-100 d-flex align-items-center justify-content-center'>
                    <h1 className='text-light'>Menu</h1>
                </div>
            </header>

            {/* Render Breakfast */}
            <div className='breakfast my-5'>
                <div className='container'>
                    <h2 className='text-center fs-1 mb-4 mb-lg-5 text-uppercase fw-bold text-success'>Breakfast</h2>
                    <div className='row flex-column-reverse flex-lg-row'>
                        <div className='col-lg-6 d-flex justify-content-center'>
                            <img src={BreakfastImg} className='img-fluid w-75 mt-4 mt-lg-0' alt="" />
                        </div>
                        <div className='col-lg-6 d-flex flex-column justify-content-around'>
                            {breakfast.map((item) => (
                                <div key={item.id}>
                                    <Card className='border-0'>
                                        <CardBody>
                                            <CardTitle className='text-center fs-3'>{item.name}</CardTitle>
                                            <CardText className='text-center fs-5'>{item.description}</CardText>
                                            <CardText className='text-center fs-3 fw-bold text-success'>{item.price}</CardText>
                                            <div className='d-flex justify-content-center'>
                                                <Button variant="danger" onClick={() => deleteItem('breakfast', item.id)}>Delete</Button>
                                            </div>
                                        </CardBody>
                                    </Card>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Render Lunch */}
            <div className='lunch bg-dark text-light py-5'>
                <div className='container'>
                    <h2 className='text-center fs-1 mb-4 mb-lg-5 text-uppercase fw-bold text-success'>Lunch</h2>
                    <div className='row'>
                        <div className='col-lg-6 d-flex flex-column justify-content-around'>
                            {lunch.map((item) => (
                                <div key={item.id}>
                                    <Card className='border-0 bg-dark text-light'>
                                        <CardBody>
                                            <CardTitle className='text-center fs-3'>{item.name}</CardTitle>
                                            <CardText className='text-center fs-5'>{item.description}</CardText>
                                            <CardText className='text-center fs-3 fw-bold text-success'>{item.price}</CardText>
                                            <div className='d-flex justify-content-center'>
                                                <Button variant="danger" onClick={() => deleteItem('lunch', item.id)}>Delete</Button>
                                            </div>
                                        </CardBody>
                                    </Card>
                                </div>
                            ))}
                        </div>
                        <div className='col-lg-6 d-flex justify-content-center'>
                            <img src={LunchImg} className='img-fluid w-75 mt-4 mt-lg-0' alt="" />
                        </div>
                    </div>
                </div>
            </div>

            {/* Render Dinner */}
            <div className='dinner my-5'>
                <div className='container'>
                    <h2 className='text-center fs-1 mb-4 mb-lg-5 text-uppercase fw-bold text-success'>Dinner</h2>
                    <div className='row flex-column-reverse flex-lg-row'>
                        <div className='col-lg-6 d-flex justify-content-center'>
                            <img src={DinnerImg} className='img-fluid w-75 mt-4 mt-lg-0' alt="" />
                        </div>
                        <div className='col-lg-6 d-flex flex-column justify-content-around'>
                            {dinner.map((item) => (
                                <div key={item.id}>
                                    <Card className='border-0'>
                                        <CardBody>
                                            <CardTitle className='text-center fs-3'>{item.name}</CardTitle>
                                            <CardText className='text-center fs-5'>{item.description}</CardText>
                                            <CardText className='text-center fs-3 fw-bold text-success'>{item.price}</CardText>
                                            <div className='d-flex justify-content-center'>
                                                <Button variant="danger" onClick={() => deleteItem('dinner', item.id)}>Delete</Button>
                                            </div>
                                        </CardBody>
                                    </Card>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {/* Render Dessert */}
            <div className='dessert bg-dark text-light py-5'>
                <div className='container'>
                    <h2 className='text-center fs-1 mb-4 mb-lg-5 text-uppercase fw-bold text-success'>Dessert</h2>
                    <div className='row'>
                        <div className='col-lg-6 d-flex flex-column justify-content-around'>
                            {dessert.map((item) => (
                                <div key={item.id}>
                                    <Card className='border-0 bg-dark text-light'>
                                        <CardBody>
                                            <CardTitle className='text-center fs-3'>{item.name}</CardTitle>
                                            <CardText className='text-center fs-5'>{item.description}</CardText>
                                            <CardText className='text-center fs-3 fw-bold text-success'>{item.price}</CardText>
                                            <div className='d-flex justify-content-center'>
                                                <Button variant="danger" onClick={() => deleteItem('dessert', item.id)}>Delete</Button>
                                            </div>
                                        </CardBody>
                                    </Card>
                                </div>
                            ))}
                        </div>
                        <div className='col-lg-6 d-flex justify-content-center'>
                            <img src={DessertImg} className='img-fluid w-75 mt-4 mt-lg-0' alt="" />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Menu;
